using System.Collections; using System.Collections.Generic; using UnityEngine;   public class PowerUpSpawner : MonoBehaviour {     public float timer;     private float seg;     public GameObject powerup;     public int posx, posy; 	// Use this for initialization 	void Awake () {         seg = 0; 	} 	// Update is called once per frame 	void Update () {         seg += 1;         posx = Random.Range(-14, 14);         posy = Random.Range(1, 46);         if (seg%timer==0)         {             Vector3 posvec = new Vector3(posx, posy, 0f);             Instantiate(powerup, posvec, Quaternion.identity);         } 	} } 
